<?php

namespace Ivoz\Cgr\Infrastructure\Persistence\Doctrine;

use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Ivoz\Cgr\Domain\Model\RatingPlan\RatingPlanInterface;
use Ivoz\Cgr\Domain\Model\RatingPlan\RatingPlanRepository;
use Ivoz\Cgr\Domain\Model\RatingPlan\RatingPlan;
use Symfony\Bridge\Doctrine\RegistryInterface;

/**
 * RatingPlanDoctrineRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RatingPlanDoctrineRepository extends ServiceEntityRepository implements RatingPlanRepository
{
    public function __construct(RegistryInterface $registry)
    {
        parent::__construct($registry, RatingPlan::class);
    }

    /**
     * @param string $tag
     * @return RatingPlanInterface
     */
    public function findOneByTag(string $tag)
    {
        $qb = $this
            ->createQueryBuilder('self')
            ->where('self.tag = :tag')
            ->setParameter('tag', $tag);

        return $qb
            ->getQuery()
            ->getSingleResult();
    }
}

